/* example using urlshortener api */

import { ApiDiscovery, ApiClient } from '../src/';

const urlshortener = async function () {
  const client = new ApiClient({
    api: 'urlshortener',
    key: 'AIzaSyD07_EyMD1S7MGNf0GkL5V7dZ8qx2NRm2U'
  });
  const { $resource } = await client.init();
  try {
    const { id } = await $resource.url.insert({
      longUrl: 'https://en.wikipedia.org/wiki/Stephen_Hawking'
    });
    console.log('result', id);
  } catch (error) {
    console.error(error);
  }
}

urlshortener();


